// Extract data from all previous nodes
const userBrief = $('Notion Trigger').item.json['Input/Brief'] || 'No brief provided';

// Get High Performers
const highPerformers = $('High Performers Search').all();
let highPerformersText = '';
let highPerformersCount = 0;

if (highPerformers && highPerformers.length > 0) {
  highPerformersText = highPerformers.map(item => {
    const post = item.json;
    highPerformersCount++;
    const postContent = post.property_post_content || post['Post Content'] || post.body || post.content || '';
    return `EXAMPLE POST ${highPerformersCount}:\n${postContent}\n---`;
  }).join('\n\n');
} else {
  highPerformersText = 'No high-performing posts available yet.';
}

// Get Branding Guidelines
const brandingData = $('Branding Guidelines').all();
let brandGuidelines = '';

if (brandingData && brandingData.length > 0) {
  const guidelines = brandingData[0].json;
  brandGuidelines = guidelines.property_guidelines || guidelines.Guidelines || guidelines.body || 'Write in an authentic, professional voice.';
} else {
  brandGuidelines = 'Write in an authentic, professional voice suitable for a coach or consultant.';
}

// Get Client Testimonials
const testimonials = $('Client Testimonials').all();
let testimonialsText = '';
let testimonialsCount = 0;

if (testimonials && testimonials.length > 0) {
  testimonialsText = testimonials.map(item => {
    const t = item.json;
    testimonialsCount++;

    // Handle Notion property naming (with property_ prefix)
    const clientName = t.property_client_name || t['Client Name'] || t.name || 'Anonymous';
    const projectType = t.property_project_type || t['Project Type'] || '';
    const result = t.property_result_metric || t['Result/Metric'] || '';
    const testimonial = t.property_testimonial || t.Testimonial || '';

    return `TESTIMONIAL ${testimonialsCount}:\n- Client: ${clientName}\n- Project Type: ${projectType}\n- Specific Result/Metric: ${result}\n- Testimonial Quote: "${testimonial}"\n---`;
  }).join('\n\n');
} else {
  testimonialsText = 'No client testimonials available yet.';
}

// Get Perplexity Research
const perplexityData = $('HTTP Request - Perplexity Research').item.json;
let perplexityResearch = '';

if (perplexityData && perplexityData.choices && perplexityData.choices[0]) {
  perplexityResearch = perplexityData.choices[0].message.content;
} else if (perplexityData && perplexityData.content) {
  perplexityResearch = perplexityData.content;
} else {
  perplexityResearch = 'No research data available.';
}

// Get Fathom Call Ideas (optional)
const fathomData = $('Fathom Call Ideas').all();
let fathomIdeas = '';

if (fathomData && fathomData.length > 0) {
  fathomIdeas = fathomData.map((item, index) => {
    const call = item.json;
    const contentAngles = call.property_content_angles || call['Content Angles'] || '';
    const keyInsights = call.property_key_insights || call['Key Insights'] || '';
    return `CALL ${index + 1}:\nContent Angles: ${contentAngles}\nKey Insights: ${keyInsights}\n---`;
  }).join('\n\n');
} else {
  fathomIdeas = 'No recent call insights available.';
}

// Build the complete context for Claude
const contextPrompt = `
You are a LinkedIn ghostwriter for a coach or consultant. Your job is to create authentic, engaging LinkedIn posts that sound like they were written by the coach/consultant themselves - NOT by an AI.

====================================
SECTION 1: THE USER'S INPUT/BRIEF
====================================

${userBrief}

====================================
SECTION 2: HIGH-PERFORMING POST EXAMPLES
====================================

Here are examples of posts that performed well. Study the voice, structure, and style:

${highPerformersText}

====================================
SECTION 3: BRAND VOICE GUIDELINES
====================================

${brandGuidelines}

====================================
SECTION 4: YOUR ACTUAL CLIENT RESULTS
====================================

These are REAL results from REAL clients. You can reference these confidently:

${testimonialsText}

CRITICAL: These are the ONLY results you can claim as "my client" or "I helped". Do not invent or exaggerate these results.

====================================
SECTION 5: INDUSTRY DATA & STATISTICS
====================================

This is research data from Perplexity about the coaching/consulting industry and the topic:

${perplexityResearch}

CRITICAL: When using this data, attribute it generically:
- "Industry research shows..."
- "Studies indicate..."
- "The coaching market has seen..."
- "Recent data suggests..."

DO NOT claim this industry data as your own client results.

====================================
SECTION 6: CONTENT IDEAS FROM RECENT CALLS
====================================

${fathomIdeas}

====================================
ANTI-HALLUCINATION RULES (CRITICAL!)
====================================

Before mentioning ANY number, percentage, or result, ask yourself:

1. Is this from "YOUR ACTUAL CLIENT RESULTS" (Section 4)?
   â†’ YES: You can say "My client achieved...", "I helped them get..."
   â†’ NO: Continue to question 2

2. Is this from "INDUSTRY DATA & STATISTICS" (Section 5)?
   â†’ YES: You must say "Research shows...", "Studies indicate..."
   â†’ NO: DO NOT INCLUDE IT (you're hallucinating)

3. Are you making up or estimating a number?
   â†’ If YES: STOP. Remove it entirely.

NEVER EVER:
- Invent client results that aren't in Section 4
- Claim industry stats (Section 5) as your own results
- Make up percentages, timeframes, or metrics
- Exaggerate results from Section 4

====================================
YOUR TASK
====================================

Create LinkedIn posts that:
1. Sound authentically like the coach/consultant (not AI-generated)
2. Use ONLY real client results from Section 4 when claiming "my work"
3. Reference industry data from Section 5 with proper attribution
4. Follow the voice and style from the examples
5. Match the brand guidelines exactly
6. Address the brief directly

If you're unsure about a data point: LEAVE IT OUT.
`;

return [{
  json: {
    contextPrompt: contextPrompt,
    userBrief: userBrief,
    testimonialsCount: testimonialsCount,
    highPerformersCount: highPerformersCount,
    hasPerplexityData: !!perplexityResearch,
    hasFathomInsights: !!fathomIdeas
  }
}];

// Get the Claude response
const claudeResponse = $input.first().json;

// Extract the content from Claude's response
let content = '';
if (typeof claudeResponse === 'string') {
  content = claudeResponse;
} else if (claudeResponse.output) {
  content = claudeResponse.output;
} else if (claudeResponse.text) {
  content = claudeResponse.text;
} else if (claudeResponse.content) {
  content = claudeResponse.content;
} else {
  content = JSON.stringify(claudeResponse);
}

// Aggressive JSON extraction
const firstBrace = content.indexOf('{');
const lastBrace = content.lastIndexOf('}');

if (firstBrace === -1 || lastBrace === -1) {
  throw new Error('No JSON object found in Claude response. Content: ' + content.substring(0, 200));
}

let jsonString = content.substring(firstBrace, lastBrace + 1);

// Remove markdown code blocks if present
const codeBlockMatch = jsonString.match(/```(?:json)?\n?([\s\S]*?)```/);
if (codeBlockMatch) {
  jsonString = codeBlockMatch[1].trim();
}

// Clean control characters
jsonString = jsonString.replace(/[\x00-\x1F\x7F-\x9F]/g, '').trim();

// Parse the JSON
let parsedData;
try {
  parsedData = JSON.parse(jsonString);
} catch (error) {
  throw new Error('Failed to parse JSON from Claude. JSON string: ' + jsonString.substring(0, 300) + '... Error: ' + error.message);
}

const posts = parsedData.posts || [];

if (posts.length === 0) {
  throw new Error('No posts found in parsed data');
}

// Helper function to split text into chunks for Notion's 2000 char limit
function splitIntoChunks(text, maxLength = 1950) {
  if (!text) return [''];

  const chunks = [];
  let remaining = text;

  while (remaining.length > 0) {
    if (remaining.length <= maxLength) {
      chunks.push(remaining);
      break;
    }

    // Try to split at natural break points
    let splitPoint = maxLength;
    const lastNewline = remaining.lastIndexOf('\n\n', maxLength);
    const lastSingleNewline = remaining.lastIndexOf('\n', maxLength);
    const lastPeriod = remaining.lastIndexOf('. ', maxLength);
    const lastSpace = remaining.lastIndexOf(' ', maxLength);

    if (lastNewline > maxLength * 0.6) {
      splitPoint = lastNewline + 2;
    } else if (lastSingleNewline > maxLength * 0.6) {
      splitPoint = lastSingleNewline + 1;
    } else if (lastPeriod > maxLength * 0.6) {
      splitPoint = lastPeriod + 2;
    } else if (lastSpace > maxLength * 0.6) {
      splitPoint = lastSpace + 1;
    }

    splitPoint = Math.min(splitPoint, maxLength);
    chunks.push(remaining.substring(0, splitPoint).trim());
    remaining = remaining.substring(splitPoint).trim();
  }

  return chunks;
}

// Format posts as Notion blocks
const notionBlocks = [];

// Add header
notionBlocks.push({
  object: 'block',
  type: 'heading_1',
  heading_1: {
    rich_text: [{
      type: 'text',
      text: { content: 'ðŸ“ Generated LinkedIn Posts' }
    }]
  }
});

notionBlocks.push({
  object: 'block',
  type: 'divider',
  divider: {}
});

// Add each post
posts.forEach((post, index) => {
  // Post heading with angle
  notionBlocks.push({
    object: 'block',
    type: 'heading_2',
    heading_2: {
      rich_text: [{
        type: 'text',
        text: { content: `ðŸ“Œ POST ${index + 1}: ${post.angle}` }
      }]
    }
  });

  // Voice score
  notionBlocks.push({
    object: 'block',
    type: 'paragraph',
    paragraph: {
      rich_text: [{
        type: 'text',
        text: { content: `Voice Score: ${post.voiceScore}/10` },
        annotations: { italic: true }
      }]
    }
  });

  // Post content (split into chunks if needed)
  const contentChunks = splitIntoChunks(post.content);
  contentChunks.forEach(chunk => {
    notionBlocks.push({
      object: 'block',
      type: 'paragraph',
      paragraph: {
        rich_text: [{
          type: 'text',
          text: { content: chunk }
        }]
      }
    });
  });

  // Divider between posts
  if (index < posts.length - 1) {
    notionBlocks.push({
      object: 'block',
      type: 'divider',
      divider: {}
    });
  }
});

// Calculate average voice score
const averageVoiceScore = (posts.reduce((sum, p) => sum + (p.voiceScore || 0), 0) / posts.length).toFixed(1);

return [{
  json: {
    notionBlocks: notionBlocks,
    postCount: posts.length,
    averageVoiceScore: averageVoiceScore,
    pageId: $('Notion Trigger').item.json.id
  }
}];
